import React from "react";
import { Link } from "react-router-dom";
import s from "./SaleProduct.module.css";
import { useDispatch } from "react-redux";
import { addToCartAcrion } from "../../reducers/cartReducer";

export default function SaleProduct({
  id,
  title,
  image,
  price,
  discont_price,
}) {
  const dispatch = useDispatch();

  const discont = Math.round((price - discont_price) / (price / 100));

  const current_price = discont_price === null ? price : <del>{price}</del>;

  return (
    <div className={s.item}>
      <div>
        <Link to={`/products/${id}`}>
          <img src={`http://localhost:3333${image}`} alt={title} />
        </Link>
        <div className={s.item_description}>
          <p className={s.discont_price}>{discont_price}</p>
          <p className={s.price}>
            <del>${price}</del>
          </p>
          <p className={s.discont}>{discont}%</p>
        </div>
        <p>{title}</p>

        <div
          className={s.add_btn}
          onClick={() =>
            dispatch(
              addToCartAcrion({ id, image, title, price, discont_price })
            )
          }
        >
          Add to cart
        </div>
      </div>
    </div>
  );
}
