import { loadAllProductsAction } from "../reducers/allProductsReducer";
import { loadSingleProductAction } from "../reducers/productsByCategoryReducer";
import { loadProductsByCategoryAction } from '../reducers/productsByCategoryReducer'

export const getAllProducts = callback => {
    fetch(`http://localhost:3333/products/all`)
    .then(res => res.json())
    .then(json => callback(loadAllProductsAction(json)))
}

export const getSingleProduct = id => {
    return dispatch => {
fetch(`http://localhost:3333/products/${id}`)
   .then(res => res.json())
   .then(json => dispatch(loadSingleProductAction(json[0])))
}
}

export const getAllProductsByCategory = id => {
    return dispatch => {
        fetch(`http://localhost:3333/categories/${id}`)
        .then(res => res.json())
        .then(json => dispatch(loadProductsByCategoryAction(json.data)))
    }
}
