const LOAD_ALL_PRODUCTS_BY_CATYGORY = 'LOAD_ALL_PRODUCTS_BY_CATYGORY'

export const loadProductsByCategoryAction = payload => ({ type: LOAD_ALL_PRODUCTS_BY_CATYGORY, payload })

export const productsByCategoryReducer = (state = [], action) => {
    if(action.type === LOAD_ALL_PRODUCTS_BY_CATYGORY){
        return action.payload
    } else {
        return state
    }
}
