import React from "react";
import NewSeason from "../../components/NewSeason/NewSeason";
import SaleProductsContainer from "../../components/SaleProductsContainer/SaleProductsContainer";
import s from "./MainPage.module.css";
import Order from "../../components/Order/Order";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import CategoryItem from "../../components/CategoryItem/CategoryItem";

export default function MainPage() {

  const categories_store = useSelector(state => state.categories);
  const products = useSelector(state => state.products)

  const random_four_products_for_sale = () => {
    const only_sale_products = products.filter(el => el.discont_price)
    const random_products = [...only_sale_products].sort(() => Math.random() - 0.5)
    return random_products.slice(0, 4)
  }

  const products_for_sale = random_four_products_for_sale()

  return (
    <div className={s.main_page}>
      <NewSeason />
      <Link to='/categories'>
        <h3>Catalog</h3>
      </Link>
      <div className={s.categories_container}>
        {
            categories_store
            .map(el => <CategoryItem key={el.id} {...el} />)
            .slice(0, 4)
        }
      </div>
      <Order />
      <div>
        {
          products_for_sale
        }
      </div>
    </div>
  );
}
