import React from "react";
import { useSelector } from "react-redux";
import CartItem from "../CartItem/CartItem";
import s from "./Cart.module.css";

export default function Cart() {
  const cart_state = useSelector((state) => state.cart);

  const total = cart_state.reduce((acc, { price, discont_price, count }) => {
    const true_price = discont_price ? discont_price : price;
    return acc + true_price * count }, 0);

  const total_saving = cart_state.reduce((acc, { price, discont_price, count }) => {
    const price_whith_discont = discont_price ? price - discont_price : ''
    return acc + price_whith_discont * count }, 0)
    .toFixed(2)

  return (
    <div className={s.cart}>
      <div>
        {cart_state.map((el) => (
          <CartItem key={el.id} {...el} />
        ))}
      </div>
      <div className={s.order}>
        <p className={s.total}>Total:    ${total}</p>
        <form action="">
          <input className={s.input} type="text" />
          <button className={s.order_btn}>Order</button>
        </form>
        <p className={s.saved}>Saved: ${total_saving}</p>
      </div>
    </div>
  );
}
