import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { useParams } from 'react-router-dom'
import { getAllProductsByCategory } from '../../requests/products_by_category_req'
import ProductsByCategoryContainer from '../../components/ProductsByCategoryContainer/ProductsByCategoryContainer'


export default function SingleCategoriesPage() {

const { id } = useParams()

const dispatch = useDispatch()

useEffect(() => dispatch(getAllProductsByCategory(id)), [id, dispatch])
//![ id ] это ID категории, полученное из useParams. Если пользователь перейдет на другую страницу категории (из /category/1 в /category/2), id изменится, и useEffect выполнится снова, чтобы загрузить новые продукты для этой категории.

  //!(id) — это аргумент,в getProductsbyCategory. Эта функция выполняет HTTP-запрос к серверу для получения данных определенной категории. Указывает, какую категорию вы хотите получить, и id категории служит для этого "указателем"



   const products_by_category_store = useSelector(state => state.productsByCategory)

  return (
    <div>
      <ProductsByCategoryContainer products={products_by_category_store.data}/>
    </div>
  )
}
