import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getSingleProduct } from "../../requests/single_product";
import s from './SingleProductPage.module.css'

export default function SingleProductPage() {

  const dispatch = useDispatch();

  const { id } = useParams();

  useEffect(() => dispatch(getSingleProduct(id)), []);

  const product = useSelector((state) => state.product);

  const { title, price, image, discont_price, description } = product;

  const discont = Math.round((price - discont_price) / (price/100)) 


  return (
    <div className={s.item}>
      <h2>{title}</h2>
      <div>
      <img src={`http://localhost:3333/${image}`} alt={title} />
      <p>${discont_price === null ? price : <del>{price}</del>}</p> 
      <p>{discont_price !== null ? `-${discont}%` : ''}</p>
      <p>{discont_price !== null ? `$${discont_price}` : ''}</p>
      <div>To cart</div>
      <p>{description}</p>
      </div>
    </div>
  );
}
