import { useSelector, useDispatch } from 'react-redux'
import { useParams } from 'react-router-dom'
import { getAllProductsByCategory } from '../../requests/categories_req'
import s from './SingleCategoruyPage.module.css'
import ProductsByCategoryContainer from '../../components/ProductsByCategoryContainer/ProductsByCategoryContainer'


export default function SingleCategoriesPage() {

const { id } = useParams()

const dispatch = useDispatch()

useEffect(() => dispatch(getAllProductsByCategory(id)), [id, dispatch])

const products_by_category_store = useSelector(state => state.productsByCategory)

// const target_categoory = products_by_category_store.find(el => el.categoryId === +id).categoryId

// const categories = useSelector(state => state.categories)

// const category_title = categories.find(el => el.id = target_categoory).title


  return (
    <div className={s.container}>
      <h2>category_title</h2>
      <ProductsByCategoryContainer products={products_by_category_store} />
    </div>
  )
}
