const ADD_SELE_PRODUCTS = 'ADD_SALE_PRODUCTS'

export const addSaleProductsAction = discont_price => ({type: ADD_SELE_PRODUCTS, payload: discont_price})

export const saleProductsReducer = (state = [], action) => {
if(action.type === ADD_SELE_PRODUCTS){
    return action.payload.filter(el => el.discont_price !== null)
} else {
    return state
}
}
