import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { useParams } from 'react-router-dom'
import { getAllProductsByCategory } from '../../requests/products_req'
import ProductsByCategoryContainer from '../../components/ProductsByCategoryContainer/ProductsByCategoryContainer'


export default function SingleCategoriesPage() {

const { id } = useParams()

const dispatch = useDispatch()

useEffect(() => dispatch(getAllProductsByCategory(id)), [id, dispatch])

   const products_by_category_store = useSelector(state => state.productsByCategory)

   const category_title = products_by_category_store.title

  return (
    <div>
      <h1></h1>
      <ProductsByCategoryContainer products={products_by_category_store} />
    </div>
  )
}
