import './App.css';
import { Routes, Route } from 'react-router-dom';
import MainPage from './pages/MainPage/MainPage'
import AllCategoriesPage from './pages/AllCategoriesPage/AllCategoriesPage'
import AllProductsPage from './pages/AllProductsPage/AllProductsPage'
import SingleProductPage from './pages/SingleProductPage/SingleProductPage';
import SingleCategoriesPage from './pages/SingleCategoriesPage/SingleCategoriesPage'
import OrdersPage from './pages/OrdersPage/OrdersPage'
import SalePage from './pages/SalePage/SalePage'
import NavBar from './components/NavBar/NavBar';
import Footer from './components/Footer/Footer'
import { getAllCategories } from './requests/categories_req';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { getAllProducts } from './requests/products_req'

function App() {

  const dispatch = useDispatch()

  useEffect(() =>  {
    dispatch(getAllCategories)
    dispatch(getAllProducts)
  }, [dispatch])

  return (
    <div className="App">
      <NavBar />
<Routes>
  <Route path='/' element={<MainPage />}/>
  <Route path='/categories' element={<AllCategoriesPage />}/>
  <Route path='/categories/:id' element={<SingleCategoriesPage />}/>
  <Route path='/products' element={<AllProductsPage />}/>
  <Route path='/products/:id' element={<SingleProductPage />}/>
  <Route path='/cart' element={<CartPage />}/>
  <Route path='/sale' element={<SalePage />}/>
</Routes>
<Footer />
    </div>
  );
}

export default App;
