            <Link to={`/products/${id}`}>
        <img src={`http://localhost:3333${image}`} alt={title} />
        <p>{title}</p>
      </Link>
      <div
        className={s.add_btn}
        onClick={() => dispatch(addToCartAcrion({ id, image, title, price, discont_price  }))}
        >Add to cart</div>
    </div>
  )
}
