import React from "react";
import NewSeason from "../../components/NewSeason/NewSeason";
import CategoriesContainer from "../../components/CategoriesContainer/CategoriesContainer";
import SaleProductsContainer from "../../components/SaleProductsContainer/SaleProductsContainer";
import s from "./MainPage.module.css";
import Order from "../../components/Order/Order";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

export default function MainPage() {

  const categories_store = useSelector(state => state.categories);

  return (
    <div className={s.main_page}>
      <NewSeason />
      <Link to='/categories'>
        <h3>Catalog</h3>
      </Link>
      <div>
        {
            categories_store
            .map(el => <CategoryItem key={el.id} {...el} />)
            .slice(0, 4)
        }
      </div>
      <Order />
      <SaleProductsContainer />
    </div>
  );
}
