import React from 'react'
import s from './ProductsByCategoryCard.module.css'
import { Link } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import { addToCartAcrion } from '../../reducers/cartReducer'

export default function ProductsByCategoryCard({ id, image, title }) {

const dispatch = useDispatch()

  return (
    <Link to={`/products/${id}`}>
    <div className={s.category_item}>
        <img src={`http://localhost:3333/${image}`} alt={ title } />
        <p>{ title }</p>
        <div 
        className={s.add_btn}
        onClick={() => dispatch(addToCartAcrion({ id, image, title }))}
        >Add to cart</div>
    </div>
    </Link>
  )
}
