import { loadProductsByCategoryAction } from '../reducers/productsByCategoryReducer'

export const getAllProductsByCategory = id => {
    return dispatch => {
        fetch(`http://localhost:3333/categories/${id}`)
        .then(res => res.json())
        .then(json => dispatch(loadProductsByCategoryAction(json.date)))
    }
}
