import React from 'react'
import { useDispatch } from 'react-redux'
import { discountedProductsAction } from '../../reducers/allProductsReducer'

export default function DiscountProductForm() {

    const dispatch = useDispatch()

    const [ checkboxChecked, setcheckboxChecked ] = useState(false)

    const handleChange = () => setcheckboxChecked(!checkboxChecked)

    const get_discounted_products = e => dispatch(discountedProductsAction(e.target.checked))
    return (

    <div>
        <label>
            <p>Discounted items</p>
            <input type="checkbox" 
            checked={checkboxChecked}
            onChange={handleChange}
            onClick={get_discounted_products}
             />
        </label>
    </div>
  )
}
